sparql:
SELECT DISTINCT ?class
WHERE {
    ?class a rdfs:Class
}
ORDER BY ?class

SELECT DISTINCT ?property
WHERE {
    ?property a rdf:Property
}
ORDER BY ?property

SELECT ?class (COUNT(?instance) as ?count)
WHERE {
    ?instance a ?class
}
GROUP BY ?class
ORDER BY DESC(?count)

SELECT ?subject ?label ?comment
WHERE {
    ?subject rdfs:label ?label .
    OPTIONAL { ?subject rdfs:comment ?comment }
}
LIMIT 20
